import React, {useState, useEffect} from 'react';
import api from '../../service/api';
import Pokemon from './pokemon';
import { Link } from 'react-router-dom';

import './style.scss';

function Pokemons() {
    const [pokemons,setPokemons] = useState([]);
    const [load, setload] = useState(false);
    const [nextUrl, setNextUrl] = useState(false);


    useEffect(() => {
        if(!load){
            api.get('pokemon').then(res => {
                console.log(res);
                const newUrl = res.data.next.substr(25,res.data.next.length);
                setNextUrl(newUrl);
                const response = res.data.results;
                if(response.length > 0){
                    setPokemons(response);
                }
            })
        setload(true);
  console.log("pokemons Pai ", pokemons);


        }


    }, [load, pokemons])

   function addPokemon(url){

    api.get(url).then(res => {
      console.log("mais pokemon ",res);
      const response = res.data.results;
        if(response.length > 0){
            setPokemons([ ...pokemons, ...response]);
        }
    })

   } 

  return (
    <div className="container-pokemons">
      {pokemons.map( (el,index) =>    
         <Pokemon key={index} url={el.url}/> 
        )} 
        <Link to="/">Voltar</Link>
        <button onClick={() => addPokemon(nextUrl)}>mais pokemons</button>
    </div>
  );
}

export default Pokemons;
