import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import api from '../../service/api';
import Modal from 'react-awesome-modal';


import './style.scss';

function SearchPokemon() {
  const [pokemon,setPokemon] = useState({});
  const [ statusModal, setStatusModal ] = useState(false);
  const [ statusModalError, setStatusModalError ] = useState(false);



  function showPokemon(name){
    setStatusModal(true);

    api.get(`pokemon/${name}`).then(res => {
      const response = res;

      if(response !== 'undefined'){
          setPokemon(response);
          setStatusModal(true);
      }else{
         setStatusModalError(true);

      }
    })
 } 

 const changeStatusModal = () =>{
  setStatusModal(!statusModal);
}

const changeStatusModalError = () =>{
  setStatusModalError(!statusModalError);
}


  return (
    <>
      <Modal visible={statusModalError} effect="fadeInUp" width="60%" height="50%"  onClickAway={changeStatusModalError}> 
            <p>Pokemon não encontrado</p>
           <a onClick={changeStatusModalError}>Fechar</a>          
       </Modal>

      <Modal visible={statusModal} effect="fadeInUp" width="60%" height="50%"  onClickAway={changeStatusModal}> 

      <div className="pokemon-item">
            <img alt={pokemon.name} src={pokemon.sprites.front_default}/>
            <h3>{pokemon.name} </h3>

            <div className="pokemon-box">
              <div className="pokemon-sizes">
                <h4> Height {pokemon.height} </h4> 
                <h4> Weight {pokemon.weight} </h4> 
              </div>
                <div className="pokemon-abilities">
                  <h4>Abilities </h4>
                  <div>
                    {pokemon.abilities.map(element => 
                      <h5> {element.ability.name} </h5>
                    )} 
                  </div>
                </div> 
                <div className="pokemon-forms">
                <h4>Forms </h4>

                {/* {pokemon.forms.map(element => 
                  <Forms url={element.url} />
                )}  */}
              </div>  
            </div>
         </div>


                <a onClick={changeStatusModal}>Fechar</a>


    </Modal>

      <div className="searchPokemon-container">
        <div className="main-box">
          <h1>Descubra tudo sobre o seu pokemon</h1>
          <h2>Nós vamos ajudar você</h2>

          <div className="search">
            <input  id="mySearch" placeholder="Digite o nome do seu pokemon" />
            <button id="myBtn" onClick={() => showPokemon()}>Search</button>    
          </div>

        <div className="seeMore">
        <Link to="pokemons">Ver todos</Link>
        </div>
        </div>
      </div>
    </>
  );
}

export default SearchPokemon;
