import React, {useState, useEffect} from 'react';
import api from '../../../service/api';



function PokemonsItens(props) {
    const [pokemonsItens,setPokemonsItens] = useState({});
    const [load, setload] = useState(true);
    const [url, seturl] = useState(props.url);



    useEffect(() => {
        if(load){

            api.get(`pokemon/${props.position}`).then(res => {
                const response = res.data;
                setPokemonsItens(response);
                console.log(pokemonsItens)

            });
            setload(false);

        }
    },[load, pokemonsItens, props.position, props.url])

// console.log(pokemonsItens)
// console.log(load)
  return (
    <div>

       {pokemonsItens.abilities ? (
         <>
            <h1>{pokemonsItens.name}</h1>
            <div>
                Habilidades:
                {pokemonsItens.abilities.ability ? (
                    <h3>{pokemonsItens.abilities.map(ele => ele.ability.name)}</h3> 
                ): (
                    <p>Ainda nao</p>
                )}
            </div> 
         </>
       ): (
           <p>Ainda nao</p>
       )}

    </div>
  );
}

export default PokemonsItens;
