import React, {useState, useEffect} from 'react';
import api from '../../../service/api';

function PokemonsItem(props) {
    const [pokemonsItem,setPokemonsItem] = useState({});
    const [load, setload] = useState(false);

    useEffect(() => {
        if(!load){
            api.get(`pokemon/${props.position}`).then(res => {
                const response = res.data;
                setPokemonsItem(response);
                setload(true);
            });
        }
        console.log("filhos", pokemonsItem);
    },[load, pokemonsItem, props.position])

  return (
    <div>
       {load ? (
         <>
            <h1>{pokemonsItem.name}</h1>

                Habilidades:
                {pokemonsItem.abilities.map(element => element.ability.name)}

         </>
       ): (
           <p>Carregando</p>
       )}
    </div>
  );
}

export default PokemonsItem;
