import React, {useState, useEffect} from 'react';
import api from '../../../service/api';



function PokemonsItens(props) {
    const [pokemonsItens,setPokemonsItens] = useState({});
    const [load, setload] = useState(false);
    const [url, seturl] = useState(props.url);



    useEffect(() => {
        if(!load){
            api.get(`pokemon/${props.position}`).then(res => {
                const response = res.data;
                setPokemonsItens(response);
            });
            setload(true);

        }
    },[load, props.position])


console.log(load)
  return (
    <div>

       {load ? (
         <>
            <h1>{pokemonsItens.name}</h1>
            <div>
                Habilidades:
                <h3>{pokemonsItens.abilities.map(ele => ele.name)}</h3>
            </div> 
         </>
       ): (
           <p>Ainda nao</p>
       )}

    </div>
  );
}

export default PokemonsItens;
