import React, {useState, useEffect} from 'react';
import api from '../../../service/api';

function Pokemon(props) {
    const [pokemon,setPokemon] = useState({});
    const [load, setload] = useState(false);

    useEffect(() => {
        if(!load){
            api.get(`pokemon/${props.position}`).then(res => {
                const response = res.data;
                setPokemon(response);
                setload(true);
            });
        }
        console.log("filhos", Pokemon);
    },[load, pokemon, props.position])

  return (
    <div ke={pokemon.id}>
       {load ? (
         <>
            <img alt={pokemon.name} src={pokemon.sprites.back_default}/>
            <h1>{pokemon.name} </h1>
            <h4> Height: {pokemon.height} </h4> 
            <h4> Weight: {pokemon.weight} </h4> 

{/*            
            {pokemon.abilities.map(element => (
                <h5>{element.ability.name}</h5>
             )} */}

         </>
       ): (
           <p>Carregando</p>
       )}
    </div>
  );
}

export default Pokemon;
